    declare @error_message nvarchar(200)           --返回的消息
    select @error_code = 0
    select @error_message = N'OK'
    --------------------------------------------------------------------------------------------------------------------------------
    --ADD YOUR CODE HERE
    --------------------------------------------------------------------------------------------------------------------------------
    --返回结果
    select @error_code "Code", @error_message "Message"
END;
